package org.pilgrim.leetcode.easy;

import java.util.HashMap;
import java.util.HashSet;
import java.util.Map;
import java.util.Set;

import org.apache.commons.lang3.time.StopWatch;

import javafx.scene.paint.Stop;

public class LongestSubstringWithAtMostKDistinctCharacters {

    public static void main(String[] args) {
        // TODO Auto-generated method stub

        System.out.println(lengthOfLongestSubstringKDistinct2("eceba", 2));
        
        
        String str = ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + ""
        + "BbAabaAbBBaaaaBbAAabB";

        StopWatch watch = new StopWatch();
        watch.start();
        System.out.println(lengthOfLongestSubstringKDistinct2(str, 34));
        watch.stop();
        System.out.println(watch.getTime());
        watch.reset();
        watch.start();
        System.out.println(lengthOfLongestSubstringKDistinct(str, 34));
        watch.stop();
        System.out.println(watch.getTime());
    }
    
    public static int lengthOfLongestSubstringKDistinct2(String s, int k) {
        if(null == s || s.isEmpty()){
            return 0;
        }
        
        if(s.length() <= k) {
            return s.length();
        }
        
        int max = 0;
        
        int start = 0;
        Map<Character, Integer> map = new HashMap<>();
        for(int i=0; i<s.length(); i++) {
            char ch = s.charAt(i);
            Integer v = map.get(ch);
            if(null == v) {
                v = 0;
            }
            v++;
            map.put(ch, v);
            
            while (map.size()>k) {
                char ch1 = s.charAt(start);
                
                Integer v1 = map.get(ch1);
                if(v1 == 1) {
                    map.remove(ch1);
                }
                else {
                    map.put(ch1, --v1);
                }
                start++;
            }
            
            max = Math.max(max, i - start + 1);
        }
        
        return max;
    }
    
    public static int lengthOfLongestSubstringKDistinct(String s, int k) {
        if(null == s || s.isEmpty() || k<=0){
            return 0;
        }
        
        int max = 0;
        
        for(int j=0; j<s.length(); j++){
            int len = foo(s,k,j);
            max = Math.max(max, len);
        }
        
        return max;
    }
    
    private static int foo(String s, int k, int j){
        StringBuilder sb = new StringBuilder(s.length());
        int len = 0;
        Set<Character> list = new HashSet<>();
        for(int i=j; i<s.length(); i++){
            char ch = s.charAt(i);
            list.add(ch);
            sb.append(ch);
            len = sb.length();
            if(list.size()>k){
                len--;
                break;
            }            
        }
        return len;
    }

}
