package org.pilgrim.base;

import static org.junit.Assert.assertEquals;
import static org.pilgrim.base.StringCompare.*;

import org.junit.Test;

public class StringCompareTest
{

    @Test
    public void testStrcmpStringString()
    {
        assertEquals(strcmp3("geeks", "g*ks"), true);
        assertEquals(strcmp3("", "g*k**s*"), true);
    }
}
